INSERT INTO `tb_notice` VALUES ('69daec8477ee4eb890db2556c6e5d438', '关于暂停发票业务办理的通知', '', '2', '', '<p><span style=\"color:rgb(68, 68, 68); font-family:宋体,verdana,arial; font-size:12px\">尊敬的客户：您好！根据湖北省国家税务局 湖北省地方税务局《关于金税三期系统上线有关事项的公告》内容，现将有关事项公告如下：全省国税系统暂停业务办理安排2016年7月27日18：00至2016年8月7日24：00，全省国税系统各级办税服务厅（含车辆购置税办税服务厅），政务中心办税窗口、委托代征单位等办税服务场所以及网上办税服务厅，移动办税系统、ARM自助办税服务终端、车辆购置税自助办税服务终端等办税服务系统，将暂停办理部分涉税业务。纳税人领用发票每月均有限额限量，因此我司在此期间无法正常领购发票。故而在2016年7月27日18:00至2016年8月7日24:00无法开具增值税专用/普通发票（全省国税系统和地税系统恢复办理时间为2016年8月8日8:30）。在这期间如您有开具发票的相关需求，请于2016年8月8日9:00后至大道物流总部开发票窗口进行办理。感谢广大客户对我司的理解与支持！对于上述情况给您带来的不便，敬请谅解。</span></p>\n', '1', '2016-10-06 17:14:25', null, '2016-10-06 17:14:25', 'admin', null, 'ddwl', '2016-07-27 00:00:00');
INSERT INTO `tb_notice` VALUES ('6ae42c311cb3425ca79d3febf17bb369', '关于上海分公司闵行营业部搬迁公告', '', '2', '', '<p><span style=\"color:rgb(68, 68, 68); font-family:宋体,verdana,arial; font-size:12px\">尊敬的新老客户，您好！因当地政府整治物流、原上海分公司闵行营业部所在物流市场关停，即日起，上海分公司闵行营业部搬迁至如下地址：上海市闵行区鹤翔路83号（近沪闵路）【距原址3公里】。请各位与客户联系时，务必告知新地址。另，闵行营业部因门面面积较小、且无操作重货能力，小批量货（单票1立方/300公斤以内、单件不超过50公斤）可以从上海分公司免费转达，大批量货可以低价配送到闵行（送货费可咨询上海到货经理胡志强、15901695928）。请大家一如既往地支持上海业务拓展，共创双赢局面！欢迎广大新老客户继续光临惠顾！</span></p>\n', '1', '2016-10-06 17:30:19', null, '2016-10-06 17:30:19', 'admin', null, 'ddwl', '2016-04-08 00:00:00');
