INSERT INTO `tb_notice` VALUES ('fcee6d55274b418a8b4b432d889c2655', '关于大道物流机动车车辆保险采购招标公告', '', '2', '', '<p><span style=\"color:rgb(68, 68, 68); font-family:宋体,verdana,arial; font-size:12px\">招标信息编号：ddwl20160531 招标信息名称：大道物流机动车车辆保险采购 招标方式 ：公开招标 评标方法：综合评估法 招标预算：无 付款方式：自行约定 投标申请人必须 具备的条件： 1、投标人经年检的营业执照副本； 2、税务登记证副本（国、地）； 3、行业准入证明资格文件； 4、总经理授权书； 报名方法及时间： 1、符合要求的供应商自拟报名表格（内容包括项目名称、联系人及电话、加盖公章）上传到916013019@qq.com邮箱进行报名。 2、为确保招标顺利进行，已报名投标人须在报名截止当日确认是否参与该项目的投标，如参与向我司发函确认；如不参与投标须向我司发退标函说明情况。 3、招标起止时间：2016年6月1日8:30时 至 2016年6月5日17:30时 4、开标时间：2016年6月6日上午9:00时整 投标报名地点：武汉市东西湖区车站村160号（团结大道与六顺路交汇处向南200米） 招标人：武汉市大道物流有限责任公司 经办人：胡先生13971287091 林先生13971068953</span></p>\n', '1', '2016-10-06 17:24:06', null, '2016-10-06 17:24:06', 'admin', null, 'ddwl', '2016-06-01 00:00:00');
